from collections import Counter


class Solution:
    def minWindow(self, s: str, t: str) -> str:
        tcnt = Counter(t)
        i = 0
        j = len(t)
        tlen = len(t)
        n = tlen
        minimum = ""
        len_min = float("inf")
        while i < len(s):
            sub = s[i:j]
            print("{0}, {1}, {2}".format(i, j, sub))
            if self.does_contain_all_substring(sub, tcnt):
                i += 1
                if len(sub) <= len_min:
                    minimum = sub
                    len_min = len(minimum)
            elif j <= len(s):
                    j += 1
            else:
                break
        return minimum

    def does_contain_all_substring(self, substring, tcnt):
        subcnt = Counter(substring)
        for k, v in tcnt.items():
            if subcnt.get(k, None):
                if subcnt.get(k) < v:
                    return False
            else:
                return False
        return True

